Lengthening Data

# Original data

df1 = df1 |&gt;
  tidyr::pivot_longer(cols = 14:249,
                      values_to = "value") |&gt; 
  tidyr::fill(value) |&gt; 
  tidyr::pivot_wider(names_from = 1,
                     values_from = value) |&gt;
  janitor::clean_names() |&gt;
  dplyr::select(ano:indicador_de_seguimiento_a_la_economia)


# Data adjusted for seasonal and calendar effects

df2 = df2 |&gt;
  tidyr::pivot_longer(cols = 2:249,
                      values_to = "value") |&gt; 
  tidyr::fill(value) |&gt; 
  tidyr::pivot_wider(names_from = 1,
                     values_from = value) |&gt;
  janitor::clean_names() |&gt;
  dplyr::select(ano:indicador_de_seguimiento_a_la_economia)
